/**
 * Layout result types for SVG rendering
 */

import { Point } from './common.js';

export interface LayoutResult {
  width: number;
  height: number;
  baseline: number;
  
  // SVG content generated by the layout engine
  svgContent: string;
  
  // Connection points for path stitching
  connectorLeft: Point;
  connectorRight: Point;
  
  // New connection-point-centric approach (optional for now)
  connectionPaths?: string[];     // Complete paths from left to right connection points
  overlayElements?: string[];     // Elements to draw on top (boxes, text, children)
}
